const getStoredThemeStyle=()=>localStorage.getItem("theme"),setThemeClass=n=>{const e=document.documentElement,t=[...e.classList].find(e=>e.match(/theme--(light|dark)/));if(!t)return;e.classList.remove(t),e.classList.add(`theme--${n}`)},setThemeStyle=e=>{localStorage.setItem("theme",e),setThemeClass(e)},switchTheme=()=>{const e=getStoredThemeStyle();switch(e){case"light":setThemeStyle("dark");break;case"dark":setThemeStyle("light");break;default:setThemeStyle("light");break}};document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".themeswitch");e.addEventListener("click",switchTheme,!1)},!1),window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",switchTheme,!1);const currThemeStyle=getStoredThemeStyle();if(currThemeStyle)setThemeStyle(currThemeStyle);else{const e=window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches;e&&setThemeStyle("dark")}